#include <stdio.h>
#include <fcntl.h>
#define MAX(a,b) (((a) > (b)) ? (a) : (b))
#define MIN(a,b) (((a) < (b)) ? (a) : (b))
/* (death) larva stage -> pupa -> maturity */
#define MAP " .',:;=*i!|lX@#"
int main(){
	fd_set fd_r;
	srand(time(0));
	struct timeval ts;
	const int wid = WIDTH,
	      hei = HEIGHT,
	      cutoff = 225, 
	      reproduce = rand() % 40 + 10, 
	      dieoff = rand() % 5 + 1,
	      move = rand() % 20 + 1, 
	      moveCost = rand() % 50 + 1, 
	      tps = 300;

	int	ix = 0,
		iy,
		i,
		j,
		life[HEIGHT][WIDTH] = {{0}},
		*flat = (int*)life,
		turn = 0,
		best, 
		bstIx, 
		bstIy, 
		limI, 
		limJ;

	ts.tv_sec = 0;
	printf("%s (%d tps)\n\n", ABOUT, tps);
	flat[rand() % (hei * wid)] = 32;

	for(;;turn++) {
		for(ix = 0; ix < hei; ix++) {
			for(iy = 0; iy < wid; iy++) {
				if( (turn&0xf) == 0) { 
					putchar(MAP[life[ix][iy] >> 4]);
				}
				if(life[ix][iy] > 0) {
					if(life[ix][iy] > board[ix][iy] && (rand() % (board[ix][iy] + 1)) < dieoff ) { /* dies */
						life[ix][iy] = 0;
					} else if(rand() % reproduce == 0) { /* reproduces */
							limI = MIN(ix + 2, hei);
							limJ = MIN(iy + 2, wid);
							for(i = MAX(ix - 1, 0); i < limI; i++){
								for(j = MAX(iy - 1, 0); j < limJ; j++){
								if(life[i][j] == 0 && (rand() % 16 == 0)) {
									life[i][j] = 1;
								}
							}
						}
					} else if(rand() % move == 0) { /* move to a better spot */
							best = board[ix][iy]; /* current position */
							limI = MIN(ix + 1, hei);
							limJ = MIN(iy + 1, wid);
							for(i = MAX(ix - 1, 0); i < limI; i++){
								for(j = MAX(iy - 1, 0); j < limJ; j++){
									if(life[i][j] == 0) {
										if(board[i][j] > best) {
											bstIx = i;
											bstIy = j;
											best = board[i][j];
										}
									}
								}
							}
							if(best != board[ix][iy]) {	/* found a new spot */
								life[bstIx][bstIy] = MIN(life[ix][iy] + 1, cutoff);
								life[ix][iy] = 0;
							}
					} else if(rand() % moveCost == 0) { /* moving is more expensive then staying */
						life[ix][iy]++; 
						if(life[ix][iy] > cutoff) {
							life[ix][iy] = cutoff;
						}
					}
				}
			}
			if((turn & 0xf) == 0) {
				putchar('\n');
			}
		}
		FD_ZERO(&fd_r);
		FD_SET(0, &fd_r);
		ts.tv_usec = 1000000 / tps;
		select(1, &fd_r, NULL, NULL, &ts);
		if(FD_ISSET(0, &fd_r)) { 
			return(0);
		}
		if((turn & 0xf) == 0) {
			printf("\033[%dA", HEIGHT);
		}
	}
	return 0;
}
